processing.clientCallback:
var connexion = new Connexion();
connexion.setParameters(new Hash({get_action:'run_scheduler'}));
connexion.sendAsync();

clientCallback.dialogOnOpen:
                           this.currentFormManager = new FormManager();
                           this.currentFormManager.params = $A([
                                    $H({name:'action',label:MessageHash['action.scheduler.1'], description:MessageHash['action.scheduler.1d'],type:'string', mandatory:true}),
                                    $H({name:'schedule',label:MessageHash['action.scheduler.2'], description:MessageHash['action.scheduler.2d'],type:'string', mandatory:true}),
                                    $H({name:'repository_id',label:MessageHash['action.scheduler.4'], description:MessageHash['action.scheduler.4d'],type:'string', mandatory:true}),
                                    $H({name:'params',label:MessageHash['action.scheduler.5'], description:MessageHash['action.scheduler.5d'],type:'string', mandatory:true})
                                ]);

                           this.currentFormManager.createParametersInputs(
                                $('scheduler-task-form'),
                                this.currentFormManager.params,
                                true
                           );

clientCallback.dialogOnComplete:
 var values = new Hash({});
this.currentFormManager.serializeParametersInputs(
     $('scheduler-task-form'),
     values,
     '');
     console.log(values);
