processing.clientCallback:
                         $$('body')[0].insert($('user_feed'));
                         $('user_feed').setStyle({
                            display:'block',
                            position:'absolute',
                            top:($('get_my_feed_button').cumulativeOffset().top + $('get_my_feed_button').getHeight() + 13) + 'px',
                            left:($('get_my_feed_button').cumulativeOffset().left - $('get_my_feed_button').getWidth()/2) + 'px'
                         });
                         modal.createTopCaret($('user_feed'));
                         $('ajxp_desktop').observeOnce("click", function(){
                            $('user_feed').fade({duration:0.3});
                         });


                         var dm = new AjxpDataModel(true);
                         var rP = new RemoteNodeProvider();
                         rP.initProvider({get_action:'get_my_feed', format:'xml'});
                         var root = new AjxpNode("/");
                         root._iNodeProvider = rP;
                         var pFactory = new PreviewFactory();
                         var container = $("feed_content");
                         pFactory.setThumbSize(32);
                         root.observeOnce("loaded", function(){
                             container.update('');
                             root.getChildren().each(function(el){
                                var div = new Element('div');
                                var img = pFactory.generateBasePreview(el);
                                div.IMAGE_ELEMENT = img;
                                div.insert(img);
                                div.insert(el.getMetadata().get("event_description"));
                                container.insert(div);
                                pFactory.enrichBasePreview(el, div);
                             });
                             pFactory.loadNextImage();
                         });
                         root.load();
